import { StatusBar } from "expo-status-bar";
import client from "../api/client";
import React, {useContext, useEffect} from "react";
import Calendar from './newQueue';
import {
    StyledContainer,
    InnerContainer,
    PageLogo,
    PageTitle,
    ExtraText,
    ExtraView,
    TextLink,
    TextLinkContent
} from './../components/styles'
import {View, Text} from 'react-native';
import {UserContext} from '../contexts/userContexts'

const {brand, derLight, primary} = Colors;

import {Octicons, Ionicons} from '@expo/vector-icons';

/*import {colors} from './../components/styles';*/
import { Colors } from "react-native/Libraries/NewAppScreen";
import { useState } from "react";
import AsyncStorage from "@react-native-async-storage/async-storage";

const Welcome = ({navigation}) => {
    const {user, Logout} = useContext(UserContext);

    const logout = async () => {
        try {
            await AsyncStorage.removeItem('token');
            Logout();
            navigation.navigate('Login');
        } catch (e) {
            console.log(e);
        }
    }
    
    return (
        

        <StyledContainer>
            <StatusBar style="dark" />
            <InnerContainer>
                <PageLogo resizeMode="cover" source = {require('./../assets/lak.jpeg')}/>
                <PageTitle>רחל ציפורניים</PageTitle>
            </InnerContainer>
            <View>
                
                <Text>ברוכה הבאה לאפליקציה {user.userName}</Text>
            </View>    
            <View style={"marginTop: 70"}>
                <Calendar/>
            </View>
            <ExtraView>
                <TextLinkContent onPress={ () => logout()}> התנתקי </TextLinkContent>
                    <ExtraText>
                            יאללה לצאת 
                    </ExtraText>
                        <TextLink>
                        </TextLink>
                    </ExtraView>
        </StyledContainer>
    );
}

export default Welcome;
