import React, { useEffect, useState, useContext } from 'react';
import { StyleSheet, View ,Button, Pressable, FlatList, TouchableOpacity, Alert, ActivityIndicator, Modal, Text} from 'react-native';
import Calendar from './newQueue';
import { Entypo, MaterialIcons, FontAwesome5, Ionicons } from '@expo/vector-icons';
import client from '../api/client';
import AsyncStorage from "@react-native-async-storage/async-storage";
import { UserContext } from '../contexts/userContexts';
import { StyledContainer } from './../components/styles'
import {LinearGradient} from 'expo-linear-gradient';
import {API, ADMIN_ID} from '@env';

const days = {  0: "ראשון",
                1: "שני", 
                2:"שלישי", 
                3: "רביעי",
                4: "חמישי", 
                5: "שישי", 
                6: "שבת"
            };
            
            
export default function MyQueues({navigation}) {
    const [queues, setQueues] = useState({});
    const {user, Logout} = useContext(UserContext);
    const [thinking, setThinking] = useState(false);

    // 
    const Queue = ({item}) => {
        const showAlert = () => {
            Alert.alert('ביטול תור', 
                        'האם את בטוחה שאת רוצה לבטל את התור?',
                        [{text:'לא'},
                        {text:'כן', onPress: ()=> deleteQueue(item._id)}])
        }
        let theTime = new Date(item.time)
        return (
            <View style={{height: 220, margin: 20, padding: 20, display: "flex", flexDirection: 'column', alignItems: 'center',elevation: 8,}}>

                <View style={{display: 'flex', flexDirection: 'column', alignContent: 'flex-start'}}>
                    <Text style={{textAlign: 'right',fontSize: 20}}>
                        {days[theTime.getDay()]} {theTime.getDate()}/{theTime.getMonth() + 1}
                    </Text>
                    <Text style={{textAlign: 'right',fontSize: 20}}>
                        {theTime.getUTCHours()}:00
                    </Text>
                </View>
                <View>
                    <Pressable style={[styles.button, styles.buttonOpen]} onPress={() => showAlert()} >
                        <Text style={{fontSize: 17}}> ביטול התור</Text>
                    </Pressable>
                </View>
            </View>
            )
        }
                
    useEffect(async () => {
        setThinking(true);
        await client.post('/events/getMyQueue', {"user" : user}, {
            headers: {
                'Content-Type': 'application/json'
            }
        }).then((response) => {
                setQueues(response.data);
                setThinking(false);
            }).catch((err) => {
                console.log(err);
            })
    }, [])

    const deleteQueue = async (id) =>{
        await client.post("/events/deleteQueue", {'user' : user, 'id': id}, 
        { headers: {
                'Content-Type': 'application/json'
            }
        })
        .then((response) =>{
            setQueues(response.data)
        })
        .catch((err) => {
            console.log(err);
        })
    }

    const logout = async () => {
        try {
            await AsyncStorage.removeItem('token');
            Logout();
            navigation.navigate('Login');
        } catch (e) {
            console.log(e);
        }
    }


    // Sign Out Alert
    const showAlertSignOut = () => {
                    console.log(API);
                        Alert.alert('התנתקות', 
                                    'האם את בטוחה שאת רוצה להתנתק?',
                                    [{text:'כן', onPress: ()=> logout()},
                                    {text:'ביטול'}])
                    }

    return (
        <StyledContainer >
            <Text style={{fontSize:19}}>התורים שלך:</Text>
            <FlatList
                data={queues}
                renderItem={({ item }) => <Queue item={item} />}
                keyExtractor={item => item._id}
            />

            {/* Calendar model */}
            <View style={"marginTop: 70"}>
                
                <Calendar/>
            </View>
            {/* Calendar model */}

            {/* Navigation Bar */}
            <View style={{display: 'flex',justifyContent: 'center', alignContent: 'center'}}>        
                <View style={{display: 'flex', alignItems: 'center'}}>
                    <View style={ styles.menuNavigator }>

                        <TouchableOpacity onPress={() => navigation.navigate("Welcome")}>
                            <Ionicons name="home-outline" size={30} color="black" />
                        </TouchableOpacity>

                        <TouchableOpacity onPress={() => navigation.navigate("MyQueues")}>
                            <MaterialIcons name="playlist-add-check" size={35} color="black" />
                        </TouchableOpacity>

                        <TouchableOpacity onPress={'https://ul.waze.com/ul?ll=31.24937992%2C34.78982806&navigate=yes&utm_campaign=default&utm_source=waze_website&utm_medium=lm_share_location'}>
                            <FontAwesome5 name="waze" size={29} color="black" />
                        </TouchableOpacity>

                        <TouchableOpacity onPress={() => showAlertSignOut()}>
                            <Entypo name="log-out" size={29} color="black" />
                        </TouchableOpacity>

                    </View>
                </View>
            </View>
            {/* Navigation Bar */}

        </StyledContainer>
    )
}

const styles = StyleSheet.create({
    linearGradient: {
        flex: 1,
        paddingLeft: 15,
        paddingRight: 15,
        borderRadius: 5,
        
        backgroundColor: "white",
        display: "flex",
        flexDirection: 'row',
        justifyContent:'space-around',
        alignItems: 'center',
        height:90,
        marginLeft: 3,
        marginRight: 3,
        marginVertical: 2
    },
    menuNavigator: {
        display: 'flex',
        flexDirection: 'row', 
        justifyContent:'space-between', 
        alignItems:'center', 
        backgroundColor: '#f2f2f2', 
        width: '95%',height:70,
        borderRadius:15,   
        shadowColor: '#000',
        shadowOffset: { width: 0, height: 2 },
        shadowOpacity: 0.5,
        shadowRadius: 2,
        elevation: 2,
        paddingHorizontal: 18
    },
    queues: {     
        borderWidth: 1,
        borderColor: "black",
        backgroundColor: "white",
        borderRadius:10,
        display: "flex",
        flexDirection: 'row',
        justifyContent:'space-around',
        alignItems: 'center',
        height:90,
        marginLeft: 3,
        marginRight: 3,
        marginVertical: 2
    },
    button: {
        backgroundColor: '#a3c6ff',
        borderRadius: 15,
        padding: 10,
        width: 105,
        alignItems:'center',  
        elevation: 8,
        marginHorizontal:24,
        height: 40,
        
    },
    buttons: {
       height: 50,
       backgroundColor: '#f1f1f1',
       alignItems: 'center',
       justifyContent: 'center',
       marginTop: 85,
       width: "100%",
       display: 'flex',
       flexDirection: 'row'
   }
});
