import React, { useEffect, useState, useContext, useRef } from 'react';
import { StyleSheet, View ,Button, Pressable, FlatList, TouchableOpacity, Image ,Modal, Text, ActivityIndicator} from 'react-native';
import CalendarPicker from 'react-native-calendar-picker';
import client from '../api/client';
import { FontAwesome } from '@expo/vector-icons';
import { UserContext } from '../contexts/userContexts';
import {API, ADMIN_ID} from '@env';
import { Overlay } from 'react-native-elements';


export default function Admin_pannel(navigation) {
    const [thinking, setThinking] = useState(false);
    const [indicator, setIndicator] = useState(false);
    const [toApear, SetApearence] = useState(false);
    const [selectedDate, setSelectedDate] = useState(new Date());
    const {user} = useContext(UserContext);
    const [Success, setSuccess] = useState(false);
    const [visible, setVisible] = useState(false);
    const [massage, setMassage] = useState(false);
    const [catchH, setCatchH] = useState([]);
    const [choousenHour, setChoosenHour] = useState(0)
    const [hours, setHours] = useState([
        { hour:9, key: '1', color:'#d3ffa3', iscatched: false, user: {}},
        { hour:10, key: '2', color:'#d3ffa3', iscatched: false, user: {}},
        { hour:11, key: '3', color:'#d3ffa3', iscatched: false, user: {}},
        { hour:12, key: '4', color:'#d3ffa3', iscatched: false, user: {}},
        { hour:13, key: '11', color:'#d3ffa3', iscatched: false, user: {}},
        { hour:14, key: '12', color:'#d3ffa3', iscatched: false, user: {}},
        { hour:15, key: '5', color:'#d3ffa3', iscatched: false, user: {}},
        { hour:16, key: '6', color:'#d3ffa3', iscatched: false, user: {}},
        { hour:17, key: '7', color:'#d3ffa3', iscatched: false, user: {}},
        { hour:18, key: '8', color:'#d3ffa3', iscatched: false, user: {}},
        { hour:19, key: '9', color:'#d3ffa3', iscatched: false, user: {}},
        { hour:20, key: '10', color:'#d3ffa3', iscatched: false, user: {}}
        
    ]);

    const addQueue = async () => {
        setThinking(true);
        console.log(ADMIN_ID);
        console.log(choousenHour);
        await client.post('/events/addQueue', {'user': user, 'time': selectedDate, 'hour': choousenHour, 'admin': ADMIN_ID} , {
            headers: {
                'Content-Type': 'application/json'
            }
        })
        .then((response) => {
            setThinking(false);
            setMassage(false);
            toggleOverlay();
            setIndicator(!indicator);
        }).catch((err) => {
            console.log(err);
        })
    }

    
    function visi(){
        SetApearence(!toApear);
    };
    
    const onDateChange = async (date) => {
        const a = new Date(date);
        setSelectedDate(a); 
    }

    useEffect(async () => {
        setThinking(true);
        await client.post('/events/AdminGetDayQueues', {"date" : selectedDate}, {
            headers: {
                'Content-Type': 'application/json'
            }
        }).then((response) => {
            setThinking(false);
            console.log(response.data.events);
            setCatchH(response.data.events);
        }).catch((err) => {
            console.log(err);
        })
    }, [selectedDate, indicator])

    const toggleOverlay = () => {
        setSuccess(true);
        setTimeout(() => {
            setSuccess(false);
        }, 3300)
    };


    const Queue = ({item}) => {
        const showAlert = () => {
            Alert.alert('ביטול תור', 
                        'האם את בטוחה שאת רוצה לבטל את התור?',
                        [{text:'לא'},
                        {text:'כן', onPress: ()=> deleteQueue(item._id)}])
        }
        let theTime = new Date(item.time)
        return (

            <View style={{height: 140, display: "flex", flexDirection: 'column', alignItems: 'center',justifyContent:"space-between", 
            shadowOffset: {
              width: 2,
              height: 2
            }
            ,shadowColor: "black", elevation : 8, backgroundColor: "white" ,shadowOpacity: 3,borderRadius: 20, padding: 3, marginHorizontal: 20, marginVertical: 5, }}>

                <View style={{ marginTop: 30,display: 'flex', flexDirection: 'row', alignContent: 'flex-start',justifyContent:"center"}}>
                    <Text style={{textAlign: 'right',fontSize: 15}}>
                        בשעה {theTime.getUTCHours()}:00 
                    </Text>
                    <Text> </Text>
                    <Text style={{textAlign: 'right',fontSize: 15}}>
                        יום {days[theTime.getDay()]} {theTime.getDate()}/{theTime.getMonth() + 1} 
                    </Text>
                </View>
                <View>
                    <Pressable style={[styles.button, styles.buttonOpen]} onPress={() => showAlert()} >
                        <Text style={{fontSize: 14}}>ביטול התור</Text>
                    </Pressable>
                </View>
            </View>
            )
        }


    return (
        <View style={{display: 'flex',justifyContent: 'center', alignContent: 'center', height: "100%"}}>
                
                <Overlay isVisible={Success} onBackdropPress={toggleOverlay}>
                    <View style={{height: 250,display: "flex", flexDirection:"column", width: 250, borderRadius:15,justifyContent:"center", alignItems:"center", backgroundColor:"#e5e5e8"}}>
                        <Image
                            source={require('../assets/success.gif')}
                            style={{height: 150,width:150}}
                        />
                        <Text style={{fontSize:15}}>התור נקבע בהצלחה!</Text>
                    </View>
                </Overlay>

                <View style={styles.container}>
                    <Modal            
                        animationType = {"slide"}  
                        transparent = {true}  
                        visible = {massage}  
                        onRequestClose = {() =>{ console.log("Modal has been closed.") } }>  

                        {/*All views of Modal*/}  
                        <View style = {styles.modal}>  
                            <Text style = {styles.text}>לקבוע לך תור לתאריך {selectedDate.getUTCDate()}/{selectedDate.getMonth() + 1} בשעה {choousenHour}:00 ?</Text>  
                            <View style={styles.buttons}>

                                    <Pressable
                                        style={[styles.button, styles.buttonOpen]}
                                        onPress={() => setMassage(!massage)}
                                        >
                                        <Text style={styles.textStyle}>ביטול</Text>
                                    </Pressable>
                                        
                                    <Pressable
                                        style={[styles.button, styles.buttonOpen]}
                                        onPress={() => addQueue()}
                                        >
                                        <Text style={styles.textStyle}>כן אני אשמח :)</Text>
                                    </Pressable>
                                    
                            </View>
                        </View>  
                    </Modal>  

                    
                    <CalendarPicker
                        startFromMonday={false}
                        weekdays={
                            [
                                'ראשון', 
                                'שני', 
                                'שלישי', 
                                'רביעי', 
                                'חמישי', 
                                'שישי', 
                                'שבת'
                        ]}

                        months={[
                            'ינואר',
                            'פברואר',
                            'מרץ',
                            'אפריל',
                            'מאי',
                            'יוני',
                            'יולי',
                            'אוגוסט',
                            'ספטמבר',
                            'אוקטובר',
                            'נובמבר',
                            'דצמבר',
                        ]}
                        onDateChange={onDateChange}
                        previousTitle = 'הקודם'
                        nextTitle="הבא"
                    />
                    <View style={styles.FLcontainer}>
                        <FlatList 
                            data={hours}
                            renderItem={({item}) => {
                                //check if hour is catched
                                let flag = false;
                                let user = {};
                                for(let x in catchH){
                                    console.log(catchH[x].user[0].username);
                                    if(catchH[x].hour == item.hour){
                                        flag = true;
                                        user = catchH[x].user[0]
                                    }
                                }
                                if(flag){
                                        item.color = '#FFD9D9';
                                        item.iscatched= true;
                                        item.user = user;
                                    }else{
                                        item.color = '#d3ffa3';
                                        item.iscatched= false;
                                        item.user = {};
                                    }
                                    <Queue item={item}/>
                                    // return  <TouchableOpacity onPress={() => {if(item.iscatched){console.log("catched " + item.hour)}else{setChoosenHour(item.hour); setMassage(!massage)}}}>
                                    
                                            
                                    //         <View style={[ styles.sectionBox, {borderColor: item.color, backgroundColor: item.color}] }>
                                    //         <Text style={{textAlign: 'center', fontSize: 20}}>{item.hour}:00 </Text>
                                    //         <Text style={{textAlign: 'center', fontSize: 20}}>{item.user.username} </Text>
                                    //         </View>
                                    //     </TouchableOpacity>
                            }}
                            
                        />
                        <ActivityIndicator
                            style={styles.loading}
                            size="large" 
                            color="#0000ff"
                            animating={thinking}
                        />
                        <TouchableOpacity activeOpacity={0.1} onPress={()=> visi(!visible)} style={ styles.touchi}>
                            <FontAwesome name="arrow-down" size={30} color="white" />
                        </TouchableOpacity>
                        
                    </View>
                </View>

        </View>

    )
}

